(function() {
  const fileHash = $.local_sha256;
  console.log('Initializing deep visibility threat hunt for hash: ' + fileHash);
  let query = "FileHash = '" + fileHash + "' AND EventType IN ('File Creation', 'File Modification', 'Process Execution', 'Network Connection') TIMEFRAME Last30Days";
  return {
    hunt_id: 'hunt-' + Math.floor(Math.random() * 10000),
    file_hash: fileHash,
    status: 'initialized',
    start_time: new Date().toISOString(),
    query: query
  };
})();

(function() {
  const huntId = $.hunt_id;
  const fileHash = $.file_hash;

  // Determine if this hash would have matches based on our mock data
  const isKnownMalware = fileHash === 'ef537f25c895bfa782526529a9b63d97aa631564d5d789c2b765448c8635fb6c' || 
                       fileHash === '8a1c7a943636a8d84c32a619d2f573a0ab45f7531ac01f3b3cbed6fb6f77d67f';

  // Number of matches depends on the hash
  const matchCount = isKnownMalware ? 
    (fileHash === 'ef537f25c895bfa782526529a9b63d97aa631564d5d789c2b765448c8635fb6c' ? 2 : 4) : 0;

  return {
    hunt_id: huntId,
    file_hash: fileHash,
    status: 'completed',
    completion_time: new Date().toISOString(),
    execution_time_seconds: Math.floor(Math.random() * 60) + 30,
    matches_found: matchCount,
    historical_presence: isKnownMalware
  };
})();

