(function() {
  const deviceId = $.device_id;
  console.log('Initializing scan for device: ' + deviceId);
  return {
    scan_id: 'scan-' + Math.floor(Math.random() * 10000),
    device_id: deviceId,
    status: 'initialized',
    start_time: new Date().toISOString()
  };
})();

(function() {
  const scanId = $.scan_id;
  const deviceId = $.device_id;

  // Simulate different scan results based on device ID pattern
  const isMalwareFound = deviceId.includes('001') || deviceId.includes('002');
  const threatSeverity = deviceId.includes('002') ? 'critical' : 'medium';

  return {
    scan_id: scanId,
    device_id: deviceId,
    status: 'completed',
    completion_time: new Date().toISOString(),
    scan_summary: {
      files_scanned: Math.floor(Math.random() * 50000) + 100000,
      scan_duration_seconds: Math.floor(Math.random() * 300) + 120,
      malware_found: isMalwareFound,
      threats_detected: isMalwareFound ? Math.floor(Math.random() * 3) + 1 : 0,
      threats_remediated: isMalwareFound ? Math.floor(Math.random() * 3) : 0,
      threat_severity: isMalwareFound ? threatSeverity : 'none'
    }
  };
})();

(function() {
  const results = $.scan_results;
  const deviceId = results.device_id;

  let threatDetails = [];

  // Generate threat details if malware was found
  if (results.scan_summary.malware_found) {
    // Create different threat types based on device ID
    if (deviceId.includes('001')) {
      threatDetails.push({
        name: 'Trojan.GenericKD.44758128',
        type: 'Trojan',
        path: 'C:\\Users\\john.doe\\Downloads\\quarterly_results.xlsm',
        hash: 'ef537f25c895bfa782526529a9b63d97aa631564d5d789c2b765448c8635fb6c',
        status: 'quarantined'
      });
    } else if (deviceId.includes('002')) {
      threatDetails.push({
        name: 'W97M.Downloader',
        type: 'Trojan Downloader',
        path: 'C:\\Users\\alice.smith\\Downloads\\invoice_template.docm',
        hash: '8a1c7a943636a8d84c32a619d2f573a0ab45f7531ac01f3b3cbed6fb6f77d67f',
        status: 'quarantined'
      });
      threatDetails.push({
        name: 'Backdoor.Win32.Generic',
        type: 'Backdoor',
        path: 'C:\\Users\\alice.smith\\AppData\\Local\\Temp\\svc_host.exe',
        hash: '2a7cee8c214ac76ba6fbbc82c59d5846a71d126a9cdcb4c036b92b106bc9cb4b',
        status: 'active'
      });
    }
  }

  return {
    device_id: deviceId,
    scan_summary: results.scan_summary,
    threat_details: threatDetails,
    remediation_status: results.scan_summary.malware_found ? 
      (results.scan_summary.threats_detected === results.scan_summary.threats_remediated ? 'complete' : 'partial') : 
      'not_required'
  };
})();
